mapper.update:
update freight_formwork_mage
set del_flag = '1',update_by = #{updateBy,jdbcType=VARCHAR},update_date = #{updateDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT
FROM freight_formwork_mage t1 WHERE t1.del_flag = '0' AND t1.memb_id = #{membId}
AND INSTR( t1.freight_formwork_name, #{freightFormworkName} )
ORDER BY ${pageInfo.orderBy}
