mapper.select:
SELECT rca.id, rca.ser_num serNum, rca.appl_date applDate, oi.ord_num ordNum, oi.buyer_id buyerId, mi1.user_name buyerAccount, oi.seller_id sellerId, mi2.user_name sellerAccount, IFNULL(rca.comm_total_amount, 0) commTotalAmount, rca.exam_diss_type examDissType, rca.cust_name custName, rca.refund_comm_state refundCommState, rca.exam_ser_bill_date examSerBillDate, rca.done_date doneDate, rca.handler_type handlerType, IFNULL(rca.platf_cost, 0) platfCost, dict.label, ROUND(IFNULL(rca.should_refund_amount, 0) - IFNULL(rca.platf_cost, 0), 2) sellerRefund FROM refund_comm_appl rca INNER JOIN ord_comm_rela ocr ON rca.ord_comm_id = ocr.id INNER JOIN order_info oi ON ocr.ord_id = oi.id INNER JOIN memb_info mi1 ON mi1.id = rca.buyer_id INNER JOIN memb_info mi2 ON mi2.id = rca.seller_id LEFT JOIN (SELECT VALUE,label FROM sys_dict WHERE TYPE = 'refund_comm_state') dict ON rca.refund_comm_state = dict.value WHERE rca.del_flag = '0'
and rca.refund_comm_state = #{refundCommState}
and rca.appl_date >= #{startDate}
and rca.appl_date <= #{endDate}
and rca.seller_id = #{sellerId}
and rca.handler_type = #{handlerType}
and rca.bs_state_type = #{bsStateType}
and rca.exam_diss_type = #{examDissType}
and ( oi.ord_num like CONCAT('%', #{query}, '%') or rca.ser_num like CONCAT('%', #{query}, '%') or mi1.user_name like CONCAT('%', #{query}, '%')
or rca.cust_name like CONCAT('%', #{query}, '%')
or mi2.user_name like CONCAT('%', #{query}, '%')
)
order by rca.appl_date desc

select rca.refund_comm_state stateType, COUNT(rca.refund_comm_state) count from refund_comm_appl rca inner join ord_comm_rela ocr on rca.ord_comm_id = ocr.id inner join order_info oi on ocr.ord_id = oi.id inner join memb_info mi1 on mi1.id = rca.buyer_id inner join memb_info mi2 on mi2.id = rca.seller_id where rca.del_flag = '0'
and rca.appl_date >= #{startDate}
and rca.appl_date <= #{endDate}
and rca.seller_id = #{sellerId}
and rca.handler_type = #{handlerType}
and rca.bs_state_type = #{bsStateType}
and rca.exam_diss_type = #{examDissType}
and ( oi.ord_num like CONCAT('%', #{query}, '%') or rca.ser_num like CONCAT('%', #{query}, '%') or mi1.user_name like CONCAT('%', #{query}, '%')
or rca.cust_name like CONCAT('%', #{query}, '%')
or mi2.user_name like CONCAT('%', #{query}, '%')
)
group by rca.refund_comm_state

select * from refund_comm_appl rca where rca.refund_comm_state = '3' and rca.exam_diss_type in ('1', '2', '3') and rca.ord_comm_id in ( select ocr.id from ord_comm_rela ocr where ocr.ord_id in
#{id}
)

mapper.update:
update refund_comm_appl
set del_flag = '1',update_by = #{updateBy,jdbcType=VARCHAR},update_date = #{updateDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT t1.id, t1.ser_num, t1.ord_num, t1.refund_counts, t1.should_refund_amount, t1.appl_date, t1.ord_comm_id, t1.refund_comm_state, t2.comm_url, t1.buyer_comm_state, t1.exam_diss_type, CASE WHEN mi.comp_name IS NULL THEN t21.user_name ELSE mi.comp_name END buyerName, CASE WHEN se.comp_name IS NULL THEN t22.user_name ELSE se.comp_name END sellerName, t21.user_name as buyerUserName, t2.comm_name, t4.pay_method_type, t1.st_amount, t1.remarks, t1.exam_ser_bill_date FROM refund_comm_appl t1 LEFT JOIN ord_comm_rela t2 ON t2.id = t1.ord_comm_id LEFT JOIN comm_pub_info t3 ON t3.id = t2.comm_pub_id LEFT JOIN order_info t4 ON t4.id = t2.ord_id left join memb_info t21 on t4.buyer_id = t21.id LEFT JOIN entp_data_info mi ON mi.memb_id = t21.id and mi.auth_exam_state = '1' and mi.del_flag != '1' left join memb_info t22 on t4.seller_id = t22.id LEFT JOIN entp_data_info se ON se.memb_id = t22.id and se.auth_exam_state = '1' and se.del_flag != '1' left join settle_ord_rela t5 on t5.ord_id = t4.id WHERE 1 = 1
and t1.refund_comm_state = #{refundCommState,jdbcType=VARCHAR}
and t1.appl_date >= #{startDate}
and t1.appl_date <= #{endDate}
and ( t1.ser_num like CONCAT('%', #{searchKey}, '%') or t1.ord_num like CONCAT('%', #{searchKey}, '%') or t2.comm_name like CONCAT('%', #{searchKey}, '%') or t3.comm_num like CONCAT('%', #{searchKey}, '%') )
and ( t1.ser_num like CONCAT('%', #{quereKey}, '%') or t1.ord_num like CONCAT('%', #{quereKey}, '%') or mi.user_name like CONCAT('%', #{quereKey}, '%') )
and t1.buyer_id = #{buyerId,jdbcType=BIGINT}
and t4.id = #{orderId,jdbcType=BIGINT}
and t5.settle_id = #{settleId,jdbcType=BIGINT}
order by t1.appl_date desc
