mapper.select:
SELECT yscm.id, appoint_memb_id, trans_mode_type, comm_id, discount_type, colle_meth_type, sq_val, onece_min, onece_max,yscm.remarks,mdi.comp_name AS 'membInfoName' from yh_ser_charge_mage yscm left join entp_data_info mdi on yscm.appoint_memb_id = mdi.memb_id WHERE yscm.del_flag = '0'
AND find_in_set(#{transModeType},yscm.trans_mode_type)
AND yscm.comm_id = #{commId}

select * from yh_ser_charge_mage yscm where yscm.del_flag = '0'
and yscm.trans_mode_type = #{transModeType}
and yscm.appoint_memb_id = #{appointMembId}

SELECT

from yh_ser_charge_mage yscm
where id = #{id,jdbcType=BIGINT}

SELECT
id,
comm_id,
discount_type
from yh_ser_charge_mage yscm
WHERE yscm.del_flag = '0'

    AND yscm.id NOT IN (#{id})

AND yscm.comm_id = #{commId}
AND yscm.trans_mode_type = #{transModeType}
AND yscm.appoint_memb_id = #{appointMembId}

mapper.update:
update yh_ser_charge_mage
set del_flag = '1',update_by = #{updateBy,jdbcType=VARCHAR},update_date = #{updateDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}
