mapper.select:
SELECT cpi.id,cpi.comm_title,cp.url,cp.comm_picture_type,cp.sort,sum(lsq.sales_volume) as 'sales_volume', sum(lsq.transaction_amount) as 'transaction_amount',count(oi.buyer_id) AS 'buyer_id' FROM comm_pub_info cpi LEFT JOIN comm_picture cp ON cp.comm_pub_id = cpi.id AND cp.comm_picture_type = '0' LEFT JOIN level_spec_quote lsq ON lsq.comm_pub_id = cpi.id LEFT JOIN ord_comm_rela ocr ON ocr.comm_pub_id = cpi.id LEFT JOIN order_info oi ON oi.id = ocr.ord_id AND oi.order_state_type != '5' WHERE cpi.del_flag = '0'
and cpi.stalls_mage_id IN
#{id}
AND cpi.create_date >= #{startTime}
AND cpi.create_date <= #{endTime}
GROUP BY cpi.id ORDER BY transaction_amount DESC

SELECT cpi.id,cpi.comm_num,goods_type,yjdf_flag, cpi.comm_title,cpi.stalls_mage_id,cpi.comm_state_type,cp.url,sm.stalls_name,cpi.comm_sale_set_id,css.comm_name, css.comm_sale_catg_id,css.sort as 'setSort',csc.catg_name,csc.sort as 'commSaleSort' FROM comm_pub_info cpi LEFT JOIN comm_sale_set css ON css.id = cpi.comm_sale_set_id LEFT JOIN comm_sale_catg csc ON csc.id = css.comm_sale_catg_id LEFT JOIN comm_picture cp ON cp.comm_pub_id = cpi.id AND cp.comm_picture_type = '0' AND cp.sort = '0' LEFT JOIN stalls_mage sm ON sm.id = cpi.stalls_mage_id WHERE cpi.del_flag = '0'
and cpi.stalls_mage_id IN
${id}
and cpi.comm_sale_set_id IN
${id}
AND (cpi.comm_num LIKE CONCAT('%', #{search}, '%') OR cpi.comm_title LIKE CONCAT('%', #{search}, '%') )
AND cpi.comm_state_type = #{commStateType}
AND cpi.comm_sale_set_id = #{commSalesSetId}
AND cpi.update_date >= #{nearlyMonth}
ORDER BY IFNULL(cpi.update_date,cpi.create_date) DESC

SELECT cpi.id,cpi.comm_num,goods_type,yjdf_flag, cpi.comm_title,cpi.stalls_mage_id,cpi.comm_state_type,cp.url,sm.stalls_name,cpi.comm_sale_set_id,css.comm_name, css.comm_sale_catg_id,css.sort as 'setSort',csc.catg_name,csc.sort as 'commSaleSort' FROM comm_pub_info cpi LEFT JOIN comm_sale_set css ON css.id = cpi.comm_sale_set_id LEFT JOIN comm_sale_catg csc ON csc.id = css.comm_sale_catg_id LEFT JOIN comm_picture cp ON cp.comm_pub_id = cpi.id AND cp.comm_picture_type = '0' AND cp.sort = '0' LEFT JOIN stalls_mage sm ON sm.id = cpi.stalls_mage_id WHERE cpi.del_flag = '0' AND cpi.base_id = #{baseId}
and cpi.comm_sale_set_id IN
${id}
AND (cpi.comm_num LIKE CONCAT('%', #{search}, '%') OR cpi.comm_title LIKE CONCAT('%', #{search}, '%') )
AND cpi.comm_state_type = #{commStateType}
AND cpi.comm_sale_set_id = #{commSalesSetId}
ORDER BY IFNULL(cpi.update_date,cpi.create_date) DESC

SELECT distinct (css.id) as 'id', css.comm_name, css.comm_sale_catg_id, css.sort as 'setSort' FROM comm_sale_catg csc LEFT JOIN comm_sale_set css ON css.comm_sale_catg_id = csc.id LEFT JOIN comm_pub_info cpi ON cpi.comm_sale_set_id = css.id WHERE cpi.del_flag = '0' AND cpi.stalls_mage_id IN
${id}
ORDER BY IFNULL(cpi.update_date,cpi.create_date) DESC

