mapper.select:
select

from comm_pub_info
where id = #{id,jdbcType=BIGINT}

select id id, comm_num commNum, memb_id membId, sale_staff_id saleStaffId, stalls_mage_id stallsMageId, comm_id commId, comm_title commTitle, comm_variet_id commVarietId, brand_mage_id brandMageId, origin_place_type originPlaceType, abroad_id abroadId, comm_local_prov commLocalProv, comm_local_city commLocalCity, comm_local_cou commLocalCou, comm_signature commSignature, Recommend recommend, notes_to_buy notesToBuy, comm_state_type commStateType, reject_reason rejectReason, exam_id examId, comm_sale_set_id commSaleSetId, version_num versionNum from comm_pub_info where del_flag = '0' and comm_state_type = '0'
and comm_id = #{commId}
and stalls_mage_id = #{stallsMageId}

select cm.*
from comm_pub_info cpi,
     comm_mage cm
where cpi.del_flag = '0'
  and cm.del_flag = '0'
  and cpi.comm_id = cm.id
  and cpi.comm_state_type = '0'
  and cpi.stalls_mage_id = #{stallsId}
  and cpi.comm_variet_id = #{commVarietId}

select b.comm_name as 'commSetName', b.saleCount, b.saleCount/a.salesCountTotal AS 'salesScale', b.saleNum, b.saleNum/a.amountTotal AS 'amountScale' FROM (SELECT css.comm_name, sum(lsq.sales_volume) AS 'saleCount', sum(lsq.transaction_amount) AS 'saleNum' FROM comm_pub_info cpi LEFT JOIN comm_sale_set css ON css.id = cpi.comm_sale_set_id LEFT JOIN level_spec_quote lsq ON lsq.comm_pub_id = cpi.id LEFT JOIN stalls_mage AS sm ON cpi.stalls_mage_id = sm.id WHERE cpi.del_flag = '0' AND sm.whole_sale_market_id = #{marketId} AND cpi.stalls_mage_id IN
${id}
AND cpi.create_date >= #{timeStart}
AND cpi.create_date <= #{timeEnd}
GROUP BY cpi.comm_sale_set_id ORDER BY saleNum DESC LIMIT 10 )b,(select sum(lsq.sales_volume) as 'salesCountTotal',sum(lsq.transaction_amount) as 'amountTotal' FROM comm_pub_info cpi LEFT JOIN comm_sale_set css ON css.id = cpi.comm_sale_set_id LEFT JOIN level_spec_quote lsq ON lsq.comm_pub_id = cpi.id) a
