mapper.select:
SELECT
from mr_ser_charge_mage mscm WHERE mscm.del_flag = '0'
AND mscm.comm_id = #{commId}

SELECT mscm.id, mscm.comm_id, mscm.colle_meth_type, mscm.sq_val, mscm.onece_min, mscm.onece_max from mr_ser_charge_mage mscm LEFT JOIN comm_mage cm ON cm.id = mscm.comm_id WHERE mscm.del_flag = '0'
AND mscm.id != #{id}
AND cm.id = #{commId}

mapper.insert:
insert into mr_ser_charge_mage (id, comm_id, colle_meth_type,
  sq_val, onece_min, onece_max,
  create_by, create_date, update_by,
  update_date, remarks, del_flag
  )
values (#{id,jdbcType=BIGINT}, #{commId,jdbcType=BIGINT}, #{colleMethType,jdbcType=VARCHAR},
  #{sqVal,jdbcType=DOUBLE}, #{oneceMin,jdbcType=DOUBLE}, #{oneceMax,jdbcType=DOUBLE},
  #{createBy,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR},
  #{updateDate,jdbcType=TIMESTAMP}, #{remarks,jdbcType=VARCHAR}, #{delFlag,jdbcType=CHAR}
  )
